import React, { useState, useEffect } from "react";
import {
  IonIcon,
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonList,
  IonItem,
  IonLabel,
  IonGrid,
  IonRow,
  IonCol,
  IonThumbnail,
  IonAvatar,
  useIonViewDidEnter,
  IonDatetime,
} from "@ionic/react";
import axios from "axios";
import { person, arrowBackCircle, arrowBack } from "ionicons/icons";
import { Link, RouteComponentProps } from "react-router-dom";
import "./AvailableJobs.css";
import "./Search.css";
import GetUser from "../components/GetUser";

export const Notifications: React.FC<RouteComponentProps> = ({ match }) => {
  //GET MY PROFILE DATA
  interface ProfileData {
    UserId: number;
  }

  const [profile, setProfile] = React.useState<ProfileData>({
    UserId: 0,
  });

  useIonViewDidEnter(() => {
    GetUser().then((data) => setProfile(data.personDataFound));
  }, []);
  console.log(profile);

  // GET ARRAY OF ALL NEW REQUESTS
  interface NotificationsDate {
    id: number;
    IsRead: boolean;
    UserActionTakenUserProfilePicURL: string;
    UserActionTakenUserActionTypeDescription: string;
    createdAt: string;
    UserActionTakenUserUserId: number;
    UserActionTakenUserName: string;
  }

  const [notifications, setNotifications] = React.useState<NotificationsDate[]>([
    {
      id: 0,
      IsRead: false,
      UserActionTakenUserProfilePicURL: "../assets/profilePic.png",
      UserActionTakenUserActionTypeDescription: "Here Yet! Go do stuff!",
      createdAt: "Yet!",
      UserActionTakenUserUserId: 0,
      UserActionTakenUserName:"horse head"
    },
  ]);

  const fetchRequests = () => {
    return axios
      .get(
        "http://localhost:3000/notifications/ListOfNotifications/" + profile.UserId,
        {
          withCredentials: true,
        }
      )
      .then((response) => {
        console.log(response);
        return response.data;
      });
  };

  React.useEffect(() => {
    fetchRequests().then((data) => setNotifications(data.listOfNotifications));
  }, [profile]);

  console.log("hellow world");

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar color="warning">
          <IonTitle className="title2">Notifications</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent>
        <IonHeader collapse="condense">
          <IonToolbar color="warning">
            <IonTitle className="title2" size="large">
              Notifications
            </IonTitle>
          </IonToolbar>
        </IonHeader>

        <IonGrid>
          <IonRow>
            <IonCol>
              <Link to="/Main">
                <IonIcon size="large" icon={arrowBackCircle} />
              </Link>
            </IonCol>
          </IonRow>
        </IonGrid>

        <IonList>
          {notifications.map((notification) => (
            <IonItem 
              // href={`${notification.NotificationLink}`} key={notification.id} 
              >
                <IonAvatar className="avatario" slot="start" >
                  <img src={notification.UserActionTakenUserProfilePicURL}  /> 
                </IonAvatar>
              <IonLabel className="labelo">
                {/* <h1>{notification.NotificationType}</h1> */}
                <h3>{notification.UserActionTakenUserActionTypeDescription}</h3>
              </IonLabel>
              <IonDatetime
                slot="end"
                displayFormat="DD-MMM-YY"
                value={notification.createdAt}
              ></IonDatetime>
            </IonItem>
          ))}
        </IonList>

      </IonContent>
    </IonPage>
  );
};

export default Notifications;
