import React, { useState, useEffect } from "react";
import {
  IonContent,
  IonLabel,
  IonButton,
  IonInput,
  IonList,
  IonItem,
  IonIcon,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonGrid,
  IonRow,
  IonCol,
  IonImg,
  IonCheckbox,
  useIonViewDidEnter,
} from "@ionic/react";
import { person, arrowBackCircle, people,camera, trash, close } from "ionicons/icons";
import axios from "axios";
import { Link, RouteComponentProps } from "react-router-dom";
import "./Profile.css";
// import "../components/GetUser";
import GetUser from "../components/GetUser";


// const checkboxList = [{ val: "Scheduler", isChecked: true }];

const EditProfile: React.FC = () => {



  interface ProfileData {
    UserId: number;
    FirstName: string;
    LastName: string;
    Email: string;
    Username: string;
    IsScheduler: boolean;
    IsDeleted: boolean;
    Company: string;
    Occupation: string;
    ProfilePicURL: string;
  }

  const [editProfile, setEditProfile] = React.useState<ProfileData>({
    UserId: 0,
    FirstName: "",
    LastName: "",
    Email: "",
    Username: "",
    IsScheduler: false,
    IsDeleted: false,
    Company: "",
    Occupation: "",
    ProfilePicURL: "",
  });

  // console.log(profile);
  useIonViewDidEnter(() => {
    GetUser().then((data) => {
      setEditProfile(data.personDataFound);
    });
  }, []);

  console.log(editProfile.FirstName);

  const handleSubmit = () => {
    // const EditProfile = {
    //   UserId: userId,
    //   FirstName: firstName,
    //   LastName: lastName,
    //   Email: email,
    //   Username: username,
    //   Company: company,
    //   Occupation: occupation,
    // };

    axios
      .put(
        "http://localhost:3000/user/PublicUpdateUserProfile/" +
          editProfile.UserId,
        {
          withCredentials: true,
          editProfile,
        }
      )
      .then((response) => {
        console.log(response);
      });
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar color="warning">
          <IonTitle className="title2">Edit Profile</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonHeader collapse="condense">
          <IonToolbar color="warning">
            <IonTitle className="title2" size="large">
              Edit Profile
            </IonTitle>
          </IonToolbar>
        </IonHeader>
        <IonGrid>
          <IonRow>
            <IonCol>
              <Link to="/Profile">
                <IonIcon size="large" icon={arrowBackCircle} />
              </Link>
            </IonCol>
          </IonRow>
        </IonGrid>
        <IonGrid>
          <form>
            <br></br>
            <IonRow className="profileGrid">
              <IonCol>
                <IonImg src={editProfile.ProfilePicURL}></IonImg>
              </IonCol>
            </IonRow>
            <IonRow className="jobGrid">
              <IonCol>
                <IonItem>
                  <IonLabel position="stacked">
                    <h1>Name:</h1>
                  </IonLabel>
                  <IonRow>
                    <IonCol>
                      <IonInput
                        type="text"
                        name="FirstName"
                        value={editProfile.FirstName}
                        placeholder="First Name"
                        onIonChange={(e: any) =>
                          setEditProfile({
                            ...editProfile,
                            FirstName: e.target.value,
                          })
                        }
                        clearInput
                      ></IonInput>
                    </IonCol>
                    <IonCol>
                      <IonInput
                        type="text"
                        name="LastName"
                        value={editProfile.LastName}
                        placeholder="Last Name"
                        onIonChange={(e: any) =>
                          setEditProfile({
                            ...editProfile,
                            LastName: e.target.value,
                          })
                        }
                        clearInput
                      ></IonInput>
                    </IonCol>
                  </IonRow>
                </IonItem>
              </IonCol>
            </IonRow>
            <IonRow className="jobGrid">
              <IonCol>
                <IonItem>
                  <IonLabel position="stacked">
                    <h1>Email:</h1>
                  </IonLabel>
                  <IonInput
                    type="text"
                    name="Email"
                    value={editProfile.Email}
                    placeholder="Email Address"
                    onIonChange={(e: any) =>
                      setEditProfile({ ...editProfile, Email: e.target.value })
                    }
                    clearInput
                  ></IonInput>
                </IonItem>
              </IonCol>
            </IonRow>
            <IonRow className="jobGrid">
              <IonCol>
                <IonItem>
                  <IonLabel position="stacked">
                    <h1>Username:</h1>
                  </IonLabel>
                  <IonInput
                    type="text"
                    name="Username"
                    value={editProfile.Username}
                    placeholder="Username"
                    onIonChange={(e: any) =>
                      setEditProfile({
                        ...editProfile,
                        Username: e.target.value,
                      })
                    }
                    clearInput
                  ></IonInput>
                </IonItem>
              </IonCol>
            </IonRow>

            <IonRow className="jobGrid">
              <IonCol>
                <IonItem>
                  <IonLabel position="stacked">
                    <h1>Company/Agency:</h1>
                  </IonLabel>
                  <IonInput
                    type="text"
                    name="Company"
                    value={editProfile.Company}
                    placeholder="Company/Ageny You Work For..."
                    onIonChange={(e: any) =>
                      setEditProfile({
                        ...editProfile,
                        Company: e.target.value,
                      })
                    }
                    clearInput
                  ></IonInput>
                </IonItem>
              </IonCol>
            </IonRow>
            <IonRow className="jobGrid">
              <IonCol>
                <IonItem>
                  <IonLabel position="stacked">
                    <h1>Occupation:</h1>
                  </IonLabel>
                  <IonInput
                    type="text"
                    name="Occupation"
                    value={editProfile.Occupation}
                    placeholder="Your Occupation..."
                    onIonChange={(e: any) =>
                      setEditProfile({
                        ...editProfile,
                        Occupation: e.target.value,
                      })
                    }
                    clearInput
                  ></IonInput>
                </IonItem>
              </IonCol>
            </IonRow>
            <IonRow>
              <IonCol>
                <IonLabel>Scheduler: </IonLabel>
                <IonCheckbox
                  className="listCol1"
                  // checked={checked}
                  // onIonChange={(e) => setChecked(e.detail.checked)}
                />
              </IonCol>
            </IonRow>
            <br></br>
            <IonRow>
              <IonCol></IonCol>
              <IonCol>
                <IonButton
                  href="/Profile"
                  // type="submit"
                  color="danger"
                  size="large"
                  expand="block"
                  fill="solid"
                  onClick={handleSubmit}
                >
                  Save
                </IonButton>
              </IonCol>
              <IonCol></IonCol>
            </IonRow>
          </form>
        </IonGrid>
      </IonContent>
    </IonPage>
  );
};

export default EditProfile;
