import React, { useState, useEffect } from "react";
import {
  IonContent,
  IonButton,
  IonLabel,
  IonRouterLink,
  IonList,
  IonItem,
  IonSpinner,
  useIonViewDidEnter,
  useIonViewDidLeave,
  useIonViewWillEnter,
  useIonViewWillLeave,
  IonIcon,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonGrid,
  IonRow,
  IonCol,
  IonImg,
  IonCheckbox,
  IonTabBar,
  IonTabButton,
  IonAvatar,
  useIonAlert,
} from "@ionic/react";
import {
  person,
  arrowBackCircle,
  people,
  ellipsisHorizontal,
  pencilSharp,
} from "ionicons/icons";
import axios from "axios";
import {
  Link,
  matchPath,
  match,
  useRouteMatch,
  RouteComponentProps,
} from "react-router-dom";
import "./AssociateProfile.css";
import GetUser from "../components/GetUser";
import { profile } from "console";


interface AssociateProfileProps
  extends RouteComponentProps<{
    id: string;
  }> {}

const AssociateProfile: React.FC<AssociateProfileProps> = ({ match }) => {
  const [present] = useIonAlert();
  const [isLoading, setIsLoading] = React.useState<boolean>(true);

  // Get Self Data
  interface SelfData {
    UserId: number;
  }

  const [Self, setSelf] = React.useState<SelfData>({
    UserId: 0,
  });

  // Get selected user data
  interface ProfileData {
    UserId: number;
    FirstName: string;
    LastName: string;
    Email: string;
    Username: string;
    IsScheduler: boolean;
    IsDeleted: boolean;
    Company: string;
    Occupation: string;
    ProfilePicURL: string;
    UserBio: string;
  }

  const [ListProfile, setListProfile] = React.useState<ProfileData>({
    UserId: 0,
    FirstName: "",
    LastName: "",
    Email: "",
    Username: "",
    IsScheduler: false,
    IsDeleted: false,
    Company: "",
    Occupation: "",
    ProfilePicURL: "",
    UserBio: "",
  });

  const fetchProfile = () => {
    return axios
      .get("http://localhost:3000/user/AssociateProfile/" + match.params.id, {
        withCredentials: true,
      })
      .then((response) => {
        return response.data;
      });
  };

  // Request Status of Selected Associate
  interface AssociationData {
    RequestStatus: string;
  }

  const [association, setAssociation] = React.useState<AssociationData>({
    RequestStatus: "",
  });

  // association.RequestStatus = "something we can see";

  const associationRequest = (listProfile: ProfileData, self: SelfData) => {
    console.log(listProfile, self);
    return axios
      .post(
        "http://localhost:3000/businessassociate/AssociateRelationshipStatus",
        {
          withCredentials: true,
          self,
          listProfile,
        }
      )
      .then((response) => {
        return response.data;
      });
  };

  useIonViewDidEnter(() => {
    fetchProfile().then((profileData) => {
      GetUser().then((selfProfileData) => {
        setSelf(selfProfileData.personDataFound);
        associationRequest(
          profileData.user,
          selfProfileData.personDataFound
        ).then((requestStatusData) => {
          setAssociation(requestStatusData.associationStatus);
        });
      });
      setListProfile(profileData.user);
    });
    setIsLoading(false);
  }, [match, ListProfile, Self, association]);

  // Request Associate component and handler
  const handleAssociateRequest = () => {
    axios
      .post(
        "http://localhost:3000/businessassociate/AssociateProfile/" +
          match.params.id,
        { withCredentials: true, Self, ListProfile }
      )
      .then((response) => {
        console.log(response);
        window.location.href = "/AssociateProfile/" + match.params.id;
      });
  };

  const NotMyAssociateProfile: React.FC = () => {
    return (
      <IonRow>
        <IonCol size="8">
          <IonButton
            onClick={handleAssociateRequest}
            color="warning"
            expand="block"
            fill="solid"
          >
            Add Associate
          </IonButton>
        </IonCol>
        <IonCol size="2">
          <IonButton
            color="medium"
            expand="block"
            fill="solid"
          >
            <IonIcon icon={ellipsisHorizontal} />
          </IonButton>
        </IonCol>
      </IonRow>
    );
  };

  // Accept or Decline Request component and handlers
  var requestResponse = {};

  const handleAcceptRequest = () => {
    requestResponse = { RequestStatus: "RequestAccepted" };
    axios
      .put("http://localhost:3000/businessassociate/UpdateRequest", {
        Self,
        ListProfile,
        requestResponse,
      })
      .then((response) => {
        console.log(response);
        window.location.href = "/AssociateProfile/" + match.params.id;
      });
  };

  const handleDeclineRequest = () => {
    requestResponse = { RequestStatus: "RequestDeclined" };
    axios
      .put("http://localhost:3000/businessassociate/UpdateRequest", {
        Self,
        ListProfile,
        requestResponse,
      })
      .then((response) => {
        console.log(response);
        window.location.href = "/AssociateProfile/" + match.params.id;
      });
  };

  const PendingAssociateProfile: React.FC = () => {
    return (
      <IonRow>
        <IonCol size="5">
          <IonButton
            onClick={handleAcceptRequest}
            color="warning"
            expand="block"
            fill="solid"
          >
            Accept Request
          </IonButton>
        </IonCol>
        <IonCol size="5">
          <IonButton
            onClick={handleDeclineRequest}
            color="medium"
            expand="block"
            fill="solid"
          >
            Deny Request
          </IonButton>
          {/* </Link> */}
        </IonCol>
      </IonRow>
    );
  };

  // PendingSent cancel request component and handler
  const handleCancelRequest = () => {
    requestResponse = { RequestStatus: "Cancelled" };
    axios
      .put("http://localhost:3000/businessassociate/UpdateRequest", {
        Self,
        ListProfile,
        requestResponse,
      })
      .then((response) => {
